import React, { useEffect } from 'react';

const AddBookDemo = () => {
  const addBook = async () => {
    const bookData = {
      title: "Microprocessors and Microcontrollers",
      author: "Sunil Mathur",
      genre: "Science",
      publishedYear: 2016,
      pages: 16,
      coverImageUrl: "https://m.media-amazon.com/images/I/51rfRY-dl4L._UF1000,1000_QL80_.jpg",
      contentUrl: "https://content.kopykitab.com/ebooks/2018/04/16972/sample/sample_16972.pdf",
    };

    const response = await fetch('http://localhost:5002/api/books', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(bookData),
    });

    if (response.ok) {
      console.log('Book added successfully!');
    } else {
      console.error('Failed to add book');
    }
  };

  useEffect(() => {
    addBook(); // Call the addBook function when the component mounts
  }, []);

  return <div>Book addition in progress...</div>;
};

export default AddBookDemo;
