import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './BookDetails.css'; // Add CSS for styling

const BookDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBookDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5002/api/books/${id}`);
        if (!response.ok) {
          throw new Error('Failed to fetch book details');
        }
        const data = await response.json();
        setBook(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchBookDetails();
  }, [id]);

  if (loading) return <div>Loading book details...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="book-details">
      {book && (
        <>
          <img src={book.coverImageUrl} alt={book.title} style={{ width: '300px', height: '450px' }} />
          <h2>{book.title}</h2>
          <p>Author: {book.author}</p>
          <p>Year: {book.year}</p>
          <p>Category: {book.category}</p>
          <p>{book.description}</p>
          <button onClick={() => navigate('/booklist')}>Back to Book List</button>
        </>
      )}
    </div>
  );
};

export default BookDetails;
