import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './search.css'; // Import styles

const Search = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [loading, setLoading] = useState(true); // Add loading state

  useEffect(() => {
    const fetchBooks = async () => {
      setLoading(true); // Set loading to true before fetching
      const response = await fetch('http://localhost:5002/api/books');
      const data = await response.json();
      setBooks(data);
      setFilteredBooks(data); // Initially show all books
      setLoading(false); // Set loading to false after fetching
    };

    fetchBooks();
  }, []);

  // Debounce function for searching
  const debounce = (func, delay) => {
    let timeoutId;
    return (...args) => {
      if (timeoutId) clearTimeout(timeoutId);
      timeoutId = setTimeout(() => {
        func.apply(null, args);
      }, delay);
    };
  };

  const handleSearch = debounce((e) => {
    const query = e.target.value;
    setSearchQuery(query);
    const filtered = books.filter(book =>
      book.title.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredBooks(filtered);
  }, 300); // Adjust the delay as needed

  return (
    <div className="search-page">
      <div className="background-image"></div> {/* Updated for dark background */}
      <div className="content">
        <h2>Search Books</h2>
        {loading ? ( // Show loading message
          <p>Loading...</p>
        ) : (
          <>
            <input
              type="text"
              placeholder="Search by title..."
              value={searchQuery}
              onChange={handleSearch}
              style={{ marginBottom: '20px', padding: '8px', width: '300px' }}
            />
            <div className="search-results">
              {filteredBooks.length > 0 ? (
                filteredBooks.map((book) => (
                  <div key={book._id} className="book-item">
                    <img src={book.coverImageUrl} alt={book.title} />
                    <h3>{book.title}</h3>
                    <Link to={`/book/${book._id}`}>Details</Link>
                  </div>
                ))
              ) : (
                <p>No books found.</p>
              )}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Search;
