import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios';
import backgroundImage from '../images/Digital-Library-1.jpg'; // Adjust path as necessary

const LoginContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  position: relative; /* Add this line to position the background image correctly */
`;

const BackgroundImage = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: url(${backgroundImage});
  background-size: cover;
  background-position: center;
  filter: blur(8px); /* Adjust blur level */
  z-index: -1;
`;

const LoginBox = styled.div`
  background: rgba(255, 255, 255, 0.8); /* Slightly opaque background */
  padding: 30px;
  border-radius: 10px;
  box-shadow: 0px 0px 15px rgba(0, 0, 0, 0.2);
  width: 100%;
  max-width: 400px;
  text-align: center;
  z-index: 1;
`;

const Input = styled.input`
  width: calc(100% - 20px);
  padding: 10px;
  margin: 10px 0;
  border: 1px solid #ccc;
  border-radius: 5px;
`;

const Button = styled.button`
  width: 100%;
  padding: 10px;
  border: none;
  border-radius: 4px;
  background: #007bff;
  color: white;
  cursor: pointer;

  &:hover {
    background: #0056b3;
  }
`;

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5001/login', { username, password });
      setMessage('Login successful');
      console.log(response.data);
      navigate('/'); // Redirect to Home component
    } catch (error) {
      if (error.response) {
        // Server responded with a status other than 200 range
        setMessage('Login failed: ' + (error.response.data.error || 'Server error'));
      } else if (error.request) {
        // Request was made but no response received
        setMessage('Login failed: No response from server');
      } else {
        // Something happened in setting up the request
        setMessage('Login failed: ' + error.message);
      }
      console.error(error);
    }
  };

  return (
    <LoginContainer>
      <BackgroundImage /> {/* Add the BackgroundImage component here */}
      <LoginBox>
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <Input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <Input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <Button type="submit">Login</Button>
        </form>
        {message && <p>{message}</p>}
        <p>
          Don't have an account? <Link to="/signup">Sign up</Link>
        </p>
      </LoginBox>
    </LoginContainer>
  );
};

export default Login;
