import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import Home from './components/Home';
import Login from './components/Login';
import Signup from './components/Signup';
import Search from './components/Search';
import EBooks from './components/EBooks';
import Feedback from './components/Feedback';
import BookList from './components/BookList'; // Import BookList
import BookDetails from './components/BookDetails'; // Import BookDetails

function App() {
  return (
    <Router>
      <Header />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/search" element={<Search />} />
        <Route path="/e-books" element={<EBooks />} />
        <Route path="/feedback" element={<Feedback />} />
        <Route path="/books" element={<BookList />} /> {/* Add BookList route */}
        <Route path="/book/:id" element={<BookDetails />} /> {/* Add BookDetails route */}
        <Route path="/add-book" element={<AddBookDemo />} />
      </Routes>
    </Router>
  );
}

export default App;
