import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './BookDetails.css';

const BookDetails = ({ isLoggedIn }) => {
  const { bookId } = useParams();
  const [book, setBook] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchBookDetails = async () => {
      const response = await fetch(`http://localhost:5002/api/books/${bookId}`);
      const data = await response.json();
      setBook(data);
    };
    fetchBookDetails();
  }, [bookId]);

  if (!book) return <div>Loading...</div>;

  return (
    <div className="book-details">
      <h1>{book.title}</h1>
      <img src={book.coverImageUrl} alt={book.title} />
      <p><strong>Author:</strong> {book.author}</p>
      <p><strong>Genre:</strong> {book.genre}</p>
      <p><strong>Description:</strong> {book.description}</p>
      {!isLoggedIn ? (
        <button onClick={() => navigate('/login')}>Login to Read</button>
      ) : (
        <button onClick={() => readBook(bookId)}>Read Book</button>
      )}
      <a onClick={() => navigate('/book-list')}>Back to List</a>
    </div>
  );
};

export default BookDetails;
