import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AddBookDemo = () => {
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [genre, setGenre] = useState('');
  const [publishedYear, setPublishedYear] = useState('');
  const [pages, setPages] = useState('');
  const [coverImageUrl, setCoverImageUrl] = useState('');
  const [contentUrl, setContentUrl] = useState(''); // Add state for Content URL
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const bookData = { 
      title, 
      author, 
      genre, 
      publishedYear, 
      pages, 
      coverImageUrl,
      contentUrl // Include contentUrl in the bookData
    };

    try {
      const response = await fetch('http://localhost:5002/api/books', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(bookData),
      });

      if (response.ok) {
        setMessage('Book added successfully!');
        navigate('/e-books'); // Redirect to E-Books list
      } else {
        setMessage('Failed to add book. Please try again.');
      }
    } catch (error) {
      console.error('Error adding book:', error);
      setMessage('An error occurred while adding the book.');
    }
  };

  return (
    <div>
      <h2>Add a New Book</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Author"
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Genre"
          value={genre}
          onChange={(e) => setGenre(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Published Year"
          value={publishedYear}
          onChange={(e) => setPublishedYear(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Pages"
          value={pages}
          onChange={(e) => setPages(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Cover Image URL"
          value={coverImageUrl}
          onChange={(e) => setCoverImageUrl(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Content URL" // Add this line for Content URL
          value={contentUrl}
          onChange={(e) => setContentUrl(e.target.value)}
          required
        />
        <button type="submit">Add Book</button>
      </form>
      {message && <p>{message}</p>} {/* Display feedback message */}
    </div>
  );
};

export default AddBookDemo;
