import React, { useState } from 'react';
import styled from 'styled-components';
import backgroundImage from '../images/Digital-Library-1.jpg'; // Importing the image

// Styled components for layout and design
const FeedbackContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  position: relative;
  overflow: hidden;
`;

const BlurredBackground = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: url(${backgroundImage}); /* Use the imported image */
  background-size: cover;
  background-position: center;
  filter: blur(4px); /* Blur effect */
  z-index: -2;
`;

const BackgroundOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(255, 255, 255, 0.5); /* Slight white overlay to enhance readability */
  z-index: -1;
`;

const FeedbackBox = styled.div`
  background: rgba(255, 255, 255, 0.8);
  padding: 30px;
  border-radius: 10px;
  box-shadow: 0px 0px 15px rgba(0, 0, 0, 0.2);
  width: 100%;
  max-width: 400px;
  text-align: center;
  z-index: 1;
`;

const Input = styled.textarea`
  width: calc(100% - 20px);
  padding: 10px;
  margin: 10px 0;
  border: 1px solid #ccc;
  border-radius: 5px;
  resize: none;
`;

const Button = styled.button`
  width: calc(100% - 20px);
  padding: 10px;
  border: none;
  border-radius: 5px;
  background-color: #007bff;
  color: white;
  font-size: 16px;
  cursor: pointer;

  &:hover {
    background-color: #0056b3;
  }
`;

const InputField = styled.input`
  width: calc(100% - 20px);
  padding: 10px;
  margin: 10px 0;
  border: 1px solid #ccc;
  border-radius: 5px;
`;

// Feedback component
const Feedback = () => {
  const [feedback, setFeedback] = useState('');
  const [bookTitle, setBookTitle] = useState('');
  const [authorName, setAuthorName] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle feedback and book recommendation submission
    console.log('Feedback:', feedback);
    console.log('Book Title:', bookTitle);
    console.log('Author Name:', authorName);

    // Reset form fields after submission
    setFeedback('');
    setBookTitle('');
    setAuthorName('');
  };

  return (
    <FeedbackContainer>
      <BlurredBackground />
      <BackgroundOverlay />
      <FeedbackBox>
        <h2>Feedback</h2>
        <form onSubmit={handleSubmit}>
          <Input
            rows="5"
            placeholder="Your feedback here..."
            value={feedback}
            onChange={(e) => setFeedback(e.target.value)}
            required
          />
          <h3>Book Recommendations</h3>
          <InputField
            type="text"
            placeholder="Book Title"
            value={bookTitle}
            onChange={(e) => setBookTitle(e.target.value)}
            required
          />
          <InputField
            type="text"
            placeholder="Author Name"
            value={authorName}
            onChange={(e) => setAuthorName(e.target.value)}
            required
          />
          <Button type="submit">Submit</Button>
        </form>
      </FeedbackBox>
    </FeedbackContainer>
  );
};

export default Feedback;
