import React from 'react';
import styled from 'styled-components';
import exampleImage from '../"C:\Users\jjyos\OneDrive\Desktop\digital-library.jpg"'; // Path to your image

const HomeContainer = styled.div`
  text-align: center;
  padding: 20px;
`;

const HeroImage = styled.img`
  width: 100%;
  height: auto;
  max-height: 500px;
  object-fit: cover;
`;

const Heading = styled.h2`
  font-size: 2.5em;
  color: #333;
`;

const Home = () => {
  return (
    <HomeContainer>
      <HeroImage src={exampleImage} alt="Hero" />
      <Heading>Welcome to e-Books!</Heading>
      <p>Your one-stop digital library for all your reading needs.</p>
    </HomeContainer>
  );
};

export default Home;
