import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './BookList.css';
import BookModal from './BookModal'; // Import the BookModal component
import Login from './Login'; // Import the Login component

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [selectedBook, setSelectedBook] = useState(null); // Manage selected book
  const [searchQuery, setSearchQuery] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [debouncedSearchQuery, setDebouncedSearchQuery] = useState(searchQuery);
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Manage login state

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await fetch('http://localhost:5002/api/books');
        if (!response.ok) {
          throw new Error('Failed to fetch books');
        }
        const data = await response.json();
        setBooks(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchBooks();
  }, []);

  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedSearchQuery(searchQuery);
    }, 300); // Adjust the debounce time as needed

    return () => {
      clearTimeout(handler);
    };
  }, [searchQuery]);

  const handleBookClick = (book) => {
    if (isLoggedIn) {
      setSelectedBook(book); // Set the clicked book as the selected one
      console.log("Selected Book:", book); // Log the selected book to verify if it's being set correctly
    } else {
      alert('You must log in to read this book.'); // Alert if not logged in
    }
  };

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  // Filter books based on the search query and selected category
  const filteredBooks = books.filter((book) => {
    const matchesSearch = book.title.toLowerCase().includes(debouncedSearchQuery.toLowerCase());
    const matchesCategory = selectedCategory === 'All' || book.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  if (loading) return <div>Loading books...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <h1>Book Collection</h1>
      <label htmlFor="search">Search by title:</label>
      <input
        id="search"
        type="text"
        placeholder="Search by title..."
        value={searchQuery}
        onChange={handleSearchChange}
      />
      <div className="categories">
        <button onClick={() => handleCategoryChange('All')}>All</button>
        <button onClick={() => handleCategoryChange('Computer Science')}>Computer Science</button>
        <button onClick={() => handleCategoryChange('Mathematics')}>Mathematics</button>
        <button onClick={() => handleCategoryChange('Civil')}>Civil</button>
        <button onClick={() => handleCategoryChange('Physics')}>Physics</button>
        <button onClick={() => handleCategoryChange('Chemical')}>Chemical</button>
        <button onClick={() => handleCategoryChange('Mechanical')}>Mechanical</button>
        <button onClick={() => handleCategoryChange('Electrical and Electronics')}>Electrical and Electronics</button>
      </div>
      <div className="book-list">
        {filteredBooks.length > 0 ? (
          filteredBooks.map((book) => (
            <div 
              key={book._id} 
              className="book-item" 
              onClick={() => handleBookClick(book)} // Add onClick to handle book click
            >
              <img src={book.coverImageUrl} alt={book.title} style={{ width: '100px' }} />
              <h2 className="book-title">{book.title}</h2>
              <Link to={`/book/${book._id}`}>Details</Link>
            </div>
          ))
        ) : (
          <p>No books found.</p>
        )}
      </div>

      {/* Render BookModal if a book is selected */}
      {selectedBook && (
        <BookModal book={selectedBook} onClose={() => setSelectedBook(null)} isLoggedIn={isLoggedIn} />
      )}

      {/* Render Login Component if user is not logged in */}
      {!isLoggedIn && <Login setIsLoggedIn={setIsLoggedIn} />} {/* Updated to pass setIsLoggedIn */}
    </div>
  );
};

export default BookList;
