import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

// Styled-components for layout and design
const SearchContainer = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  height: '100vh',
  position: 'relative',
  overflow: 'hidden',
};

const BlurredBackground = {
  position: 'absolute',
  top: 0,
  left: 0,
  width: '100%',
  height: '100%',
  backgroundImage: 'url(../images/Digital-Library-1.jpg)', // Update this path as necessary
  backgroundSize: 'cover',
  backgroundPosition: 'center',
  filter: 'blur(7px)',
  zIndex: -2,
};

const BackgroundOverlay = {
  position: 'absolute',
  top: 0,
  left: 0,
  width: '100%',
  height: '100%',
  backgroundColor: 'rgba(255, 255, 255, 0.5)', // Slight white overlay for readability
  zIndex: -1,
};

const Content = {
  position: 'relative',
  zIndex: 1,
  textAlign: 'center',
  padding: '20px',
};

const SearchInput = {
  marginBottom: '20px',
  padding: '8px',
  width: '300px',
};

const SearchResults = {
  marginTop: '20px',
};

const BookItem = {
  margin: '10px 0',
  textAlign: 'left',
};

const Search = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBooks = async () => {
      setLoading(true);
      const response = await fetch('http://localhost:5002/api/books');
      const data = await response.json();
      setBooks(data);
      setFilteredBooks(data); // Initially show all books
      setLoading(false);
    };

    fetchBooks();
  }, []);

  // Debounce function for searching
  const debounce = (func, delay) => {
    let timeoutId;
    return (...args) => {
      if (timeoutId) clearTimeout(timeoutId);
      timeoutId = setTimeout(() => {
        func.apply(null, args);
      }, delay);
    };
  };

  const handleSearch = debounce((e) => {
    const query = e.target.value;
    setSearchQuery(query);
    const filtered = books.filter(book =>
      book.title.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredBooks(filtered);
  }, 300);

  return (
    <div style={SearchContainer}>
      <div style={BlurredBackground} />
      <div style={BackgroundOverlay} />
      <div style={Content}>
        <h2>Search Books</h2>
        {loading ? (
          <p>Loading...</p>
        ) : (
          <>
            <input
              type="text"
              placeholder="Search"
              value={searchQuery}
              onChange={handleSearch}
              style={SearchInput}
            />
            <div style={SearchResults}>
              {filteredBooks.length > 0 ? (
                filteredBooks.map((book) => (
                  <div key={book._id} style={BookItem}>
                    <img src={book.coverImageUrl} alt={book.title} />
                    <h3>{book.title}</h3>
                    <Link to={`/book/${book._id}`}>Details</Link>
                  </div>
                ))
              ) : (
                <p>No books found.</p>
              )}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Search;
