import React, { useState } from 'react';

const AddBook = () => {
  const [formData, setFormData] = useState({
    title: '',
    author: '',
    genre: '',
    publishedYear: '',
    pages: '',
    coverImageUrl: '', // Add this field for the cover image URL
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:5002/api/books', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    });

    if (response.ok) {
      alert('Book added successfully!');
      setFormData({ title: '', author: '', genre: '', publishedYear: '', pages: '', coverImageUrl: '' }); // Reset form after submission
    } else {
      alert('Error adding book');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="title" value={formData.title} onChange={handleChange} placeholder="Title" required />
      <input type="text" name="author" value={formData.author} onChange={handleChange} placeholder="Author" required />
      <input type="text" name="genre" value={formData.genre} onChange={handleChange} placeholder="Genre" required />
      <input type="number" name="publishedYear" value={formData.publishedYear} onChange={handleChange} placeholder="Published Year" required />
      <input type="number" name="pages" value={formData.pages} onChange={handleChange} placeholder="Pages" required />
      <input type="text" name="coverImageUrl" value={formData.coverImageUrl} onChange={handleChange} placeholder="Cover Image URL" required />
      <button type="submit">Add Book</button>
    </form>
  );
};

export default AddBook;
