import React, { useState } from 'react';
import styled from 'styled-components';

// Styled components for layout and design
const FeedbackContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  position: relative;
`;

const BackgroundColor = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: #f0f0f0; /* Your desired background color */
  z-index: -1;
`;

const FeedbackBox = styled.div`
  background: rgba(255, 255, 255, 0.8);
  padding: 30px;
  border-radius: 10px;
  box-shadow: 0px 0px 15px rgba(0, 0, 0, 0.2);
  width: 100%;
  max-width: 400px;
  text-align: center;
  z-index: 1;
`;

const Input = styled.textarea`
  width: calc(100% - 20px);
  padding: 10px;
  margin: 10px 0;
  border: 1px solid #ccc;
  border-radius: 5px;
  resize: none;
`;

const Button = styled.button`
  width: calc(100% - 20px);
  padding: 10px;
  border: none;
  border-radius: 5px;
  background-color: #007bff;
  color: white;
  font-size: 16px;
  cursor: pointer;

  &:hover {
    background-color: #0056b3;
  }
`;

const BookRecommendations = styled.div`
  margin-top: 20px;
  text-align: left;
`;

const Book = styled.div`
  margin: 5px 0;
`;

// Feedback component
const Feedback = () => {
  const [feedback, setFeedback] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle feedback submission logic (e.g., sending to an API)
    console.log(feedback);
    // Reset feedback state after submission
    setFeedback('');
  };

  return (
    <FeedbackContainer>
      <BackgroundColor />
      <FeedbackBox>
        <h2>Feedback</h2>
        <form onSubmit={handleSubmit}>
          <Input
            rows="5"
            placeholder="Your feedback here..."
            value={feedback}
            onChange={(e) => setFeedback(e.target.value)}
            required
          />
          <Button type="submit">Submit</Button>
        </form>

        {/* Book Recommendations Section */}
        <BookRecommendations>
          <h3>Book Recommendations</h3>
          <Book><strong>Title:</strong> Deep Learning <strong>Author:</strong> Ian Goodfellow</Book>
          <Book><strong>Title:</strong> The Clean Coder <strong>Author:</strong> Robert C. Martin</Book>
          <Book><strong>Title:</strong> You Don't Know JS <strong>Author:</strong> Kyle Simpson</Book>
          <Book><strong>Title:</strong> Eloquent JavaScript <strong>Author:</strong> Marijn Haverbeke</Book>
        </BookRecommendations>
      </FeedbackBox>
    </FeedbackContainer>
  );
};

export default Feedback;
