import React from 'react';
import styled from 'styled-components';
import backgroundImage from '../images/Digital-Library-1.jpg'; // Adjust path based on your directory structure

const LoginContainer = styled.div`
  position: relative;
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
  background-color: #f0f0f0; /* Light background for contrast */
`;

const BackgroundImage = styled.img`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  object-fit: cover;
  filter: blur(8px);
  z-index: -1;
`;

const LoginBox = styled.div`
  background: rgba(255, 255, 255, 0.9); /* Slightly more opaque for readability */
  padding: 30px;
  border-radius: 10px;
  box-shadow: 0px 0px 15px rgba(0, 0, 0, 0.2); /* Increased shadow for better separation */
  width: 100%;
  max-width: 400px;
  text-align: center;
  z-index: 1; /* Ensure this is above the background */
`;

const Input = styled.input`
  width: calc(100% - 20px); /* Adjust width to account for padding */
  padding: 10px;
  margin: 10px 0;
  border: 1px solid #ccc;
  border-radius: 5px;
`;

const Button = styled.button`
  width: calc(100% - 20px); /* Adjust width to account for padding */
  padding: 10px;
  border: none;
  border-radius: 5px;
  background-color: #007bff;
  color: white;
  font-size: 16px;
  cursor: pointer;

  &:hover {
    background-color: #0056b3;
  }
`;

const Login = () => {
  return (
    <LoginContainer>
      <BackgroundImage src={backgroundImage} alt="Background" />
      <LoginBox>
        <h2>Login</h2>
        <form>
          <Input type="text" placeholder="Username" />
          <Input type="password" placeholder="Password" />
          <Button type="submit">Login</Button>
        </form>
      </LoginBox>
    </LoginContainer>
  );
};

export default Login;
