import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Updated useHistory to useNavigate
import styled from 'styled-components';
import axios from 'axios';
import backgroundImage from '../images/Digital-Library-1.jpg'; // Adjust path as necessary

const LoginContainer = styled.div`
  position: relative;
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
`;

const BackgroundImage = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: url(${backgroundImage});
  background-size: cover;
  background-position: center;
  filter: blur(8px); /* Adjust blur level */
  z-index: -1;
`;

const LoginBox = styled.div`
  background: rgba(255, 255, 255, 0.8); /* Slightly opaque background */
  padding: 30px;
  border-radius: 10px;
  box-shadow: 0px 0px 15px rgba(0, 0, 0, 0.2);
  width: 100%;
  max-width: 400px;
  text-align: center;
  z-index: 1;
`;

const Input = styled.input`
  width: calc(100% - 20px);
  padding: 10px;
  margin: 10px 0;
  border: 1px solid #ccc;
  border-radius: 5px;
`;

const Button = styled.button`
  width: calc(100% - 20px);
  padding: 10px;
  border: none;
  border-radius: 5px;
  background-color: #007bff;
  color: white;
  font-size: 16px;
  cursor: pointer;

  &:hover {
    background-color: #0056b3;
  }
`;

const SignupOption = styled.p`
  margin-top: 10px;
  font-size: 14px;
  color: #333;
`;

const SignupLink = styled(Link)`
  display: inline-block;
  padding: 10px;
  border-radius: 5px;
  background-color: #28a745;
  color: white;
  text-decoration: none;
  font-size: 16px;

  &:hover {
    background-color: #218838;
  }
`;

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/login', { username, password });
      setMessage('Login successful');
      console.log(response.data);
      navigate('/home'); // Redirect to Home component
    } catch (error) {
      setMessage('Login failed: ' + error.response.data.error);
      console.error(error);
    }
  };

  return (
    <LoginContainer>
      <BackgroundImage />
      <LoginBox>
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <Input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <Input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <Button type="submit">Login</Button>
        </form>
        {message && <p>{message}</p>}
        <SignupOption>
          Don't have an account?{' '}
          <SignupLink to="/signup">Sign Up</SignupLink>
        </SignupOption>
      </LoginBox>
    </LoginContainer>
  );
};

export default Login;
