import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import './BookDetails.css'; // Import your CSS file for styles

const BookDetails = () => {
  const { id } = useParams();
  const [book, setBook] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true); // State to track loading

  useEffect(() => {
    const fetchBookDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5002/api/books/${id}`);
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setBook(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false); // Set loading to false regardless of success or failure
      }
    };

    fetchBookDetails();
  }, [id]);

  if (loading) return <div className="spinner">Loading...</div>; // Loading indicator
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="book-details">
      <Link to="/">Back to Book List</Link>
      <h1>{book.title}</h1>
      <img src={book.coverImageUrl} alt={`Cover of ${book.title} by ${book.author}`} style={{ maxWidth: '100%', height: 'auto', borderRadius: '4px' }} />
      <p><strong>Author:</strong> {book.author}</p>
      <p><strong>Genre:</strong> {book.genre}</p>
      <p><strong>Published Year:</strong> {book.publishedYear}</p>
      <p><strong>Pages:</strong> {book.pages}</p>
      {book.contentUrl && (
        <a href={book.contentUrl} target="_blank" rel="noopener noreferrer">Read Book</a>
      )}
    </div>
  );
};

export default BookDetails;
