import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const BookList = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    const fetchBooks = async () => {
      const response = await fetch('http://localhost:5002/api/books'); // Adjust based on your API server port
      const data = await response.json();
      setBooks(data);
    };

    fetchBooks();
  }, []);

  return (
    <div>
      <h1>Book Collection</h1>
      <div className="book-list">
        {books.map((book) => (
          <div key={book._id} className="book-item">
            <Link to={`/book/${book._id}`}>
              <h2>{book.title}</h2>
              <img src={book.coverImageUrl} alt={book.title} /> {/* Ensure you have coverImageUrl in your book model */}
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BookList;
