// Feedback.js

import React, { useState } from 'react';
import './Feedback.css'; // Import your CSS file for styling

const Feedback = () => {
  const [feedback, setFeedback] = useState('');
  const [recommendedBook, setRecommendedBook] = useState('');
  const [bookTitle, setBookTitle] = useState('');
  const [authorName, setAuthorName] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
    console.log('Feedback:', feedback);
    console.log('Recommended Book:', recommendedBook, 'by', authorName);
    // Reset form fields
    setFeedback('');
    setRecommendedBook('');
    setBookTitle('');
    setAuthorName('');
  };

  return (
    <div className="feedback-container">
      <h2>Feedback Page</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="feedback">Your Feedback:</label>
          <textarea
            id="feedback"
            value={feedback}
            onChange={(e) => setFeedback(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="recommendedBook">Recommend a Book:</label>
          <input
            type="text"
            id="recommendedBook"
            value={recommendedBook}
            onChange={(e) => setRecommendedBook(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="bookTitle">Book Title:</label>
          <input
            type="text"
            id="bookTitle"
            value={bookTitle}
            onChange={(e) => setBookTitle(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="authorName">Author Name:</label>
          <input
            type="text"
            id="authorName"
            value={authorName}
            onChange={(e) => setAuthorName(e.target.value)}
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default Feedback;
