import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './BookList.css'; // Import your CSS file for styling

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await fetch('http://localhost:5002/api/books');
        if (!response.ok) {
          throw new Error('Failed to fetch books');
        }
        const data = await response.json();
        setBooks(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false); // Set loading to false once fetch is done
      }
    };

    fetchBooks();
  }, []);

  // Handle search input change
  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  // Filter books based on the search query
  const filteredBooks = books.filter((book) =>
    book.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  if (loading) return <div>Loading books...</div>; // Loading indicator
  if (error) return <div>Error: {error}</div>; // Display error message

  return (
    <div>
      <h1>Book Collection</h1>
      <input
        type="text"
        placeholder="Search by title..."
        value={searchQuery}
        onChange={handleSearchChange}
      />
      <div className="book-list">
        {filteredBooks.length > 0 ? (
          filteredBooks.map((book) => (
            <div key={book._id} className="book-item">
              <img src={book.coverImageUrl} alt={book.title} style={{ width: '100px' }} />
              <h2 className="book-title">{book.title}</h2> {/* Add class for styling */}
              <Link to={`/book/${book._id}`}>Details</Link>
            </div>
          ))
        ) : (
          <p>No books found.</p>
        )}
      </div>
    </div>
  );
};

export default BookList;
