import React from 'react';
import './BookModal.css'; // Import CSS for styling

const BookModal = ({ book, onClose }) => {
  if (!book) return null; // If no book is selected, return null

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <img src={book.coverImageUrl} alt={book.title} style={{ width: '150px', marginBottom: '10px' }} /> {/* Image at the top */}
        <h2>{book.title}</h2>
        <p><strong>Author:</strong> {book.author}</p>
        <p><strong>Genre:</strong> {book.genre}</p>
        <p><strong>Published Year:</strong> {book.publishedYear}</p>
        <p><strong>Pages:</strong> {book.pages}</p>
        <a href={book.contentUrl} target="_blank" rel="noopener noreferrer">
          <button>Read Book</button> {/* Add Read Book button */}
        </a>
        <button onClick={onClose}>Close</button>
      </div>
    </div>
  );
};

export default BookModal;
